insert into films(name, description, "year", genre_id, minimal_age, duration_in_minutes, file_id) values('Феррари', 'Об автомобилях Ferrari мечтают миллионы, а имеют единицы. Это символ роскошной жизни, известный во всем мире. Но блестящий фасад скрывает трагическую историю основателя компании Энцо Феррари. Семейные проблемы, финансовый кризис, ужасные аварии и даже гнев Ватикана – все это в свое время преодолел гениальный конструктор и бизнесмен, чтобы вписать свое имя в историю автопрома навсегда.', 2023, 5, 18, 130, 2);
insert into films(name, description, "year", genre_id, minimal_age, duration_in_minutes, file_id) values('Холоп 2', 'Гриша, бывший мажор, побывавший холопом и ставший человеком, после путешествия в «прошлое» чутко реагирует на любую несправедливость. И, конечно, не может пройти мимо беспредела, который творит наглая и избалованная Катя. Ничего удивительного, что вскоре мажорка обнаруживает себя в другом времени…', 2024, 1, 12, 119, 3);
insert into films(name, description, "year", genre_id, minimal_age, duration_in_minutes, file_id) values('Последний наемник', 'Легендарный наемный убийца Финбар решает, что он уже не так хорош и ему пора отойти от дел. Но однажды в город, где он залег на дно, наведывается банда и начинает беспредел. Финбару ничего не остается, как встать на сторону правосудия и снова взяться за оружие. И на этот раз он рискует не только собой, но и всеми, кто ему дорог.', 2024, 3, 18, 107, 4);
insert into films(name, description, "year", genre_id, minimal_age, duration_in_minutes, file_id) values('Три богатыря', 'По сказкам мы знаем, что было давным-давно, но что было еще давным-давнее? Трем богатырям предстоит узнать ответ на этот вопрос, хоть они его и не задавали. Тут такое началось, что игогошеньки! Горыныч вдруг находит динозаврика, а Алеша Попович с Князем и конем Юлием буквально проваливаются сквозь землю. Теперь надо срочно понять, как вернуть их назад в будущее. А главное, узнать, кто или что такое загадочный Пуп Земли', 2023, 2, 6, 85, 5);
insert into films(name, description, "year", genre_id, minimal_age, duration_in_minutes, file_id) values('Астрал', 'Старшеклассник Ракка обладает сверхъестественным даром видеть души умерших. Когда он переходит в новую школу, его начинает преследовать призрак таинственной девушки. Парню удается выяснить, что она мстит своим обидчикам за буллинг. Однако вскоре он понимает, что ее сила растет: теперь она хочет забрать все души, опасность нависла над всей школой.', 2024, 4, 18, 106, 1);